// Storage IPC Wrapper (JS fallback for TS errors)

const { ipcMain } = require('electron');

// Mock storage service
const storage = new Map();

const storageService = {
  async get(key) {
    return storage.get(key) || null;
  },
  async set(key, value) {
    storage.set(key, value);
    return { success: true };
  },
  async export() {
    const data = Object.fromEntries(storage);
    return { data, timestamp: Date.now() };
  }
};

function registerStorageIPC() {
  ipcMain.handle('knoux.storage.get', async (evt, key) => {
    try {
      const value = await storageService.get(key);
      return { ok: true, data: value };
    } catch (err) {
      return { ok: false, error: String(err) };
    }
  });

  ipcMain.handle('knoux.storage.set', async (evt, key, value) => {
    try {
      await storageService.set(key, value);
      return { ok: true };
    } catch (err) {
      return { ok: false, error: String(err) };
    }
  });

  ipcMain.handle('knoux.storage.export', async () => {
    try {
      const result = await storageService.export();
      return { ok: true, data: result };
    } catch (err) {
      return { ok: false, error: String(err) };
    }
  });

  console.log('✅ Storage IPC handlers registered');
}

module.exports = { registerStorageIPC };
