/**
 * Regular expression patterns
 */
export const PATTERNS = {
  // Code patterns
  FUNCTION_DEF: /(?:function\s+(\w+)|const\s+(\w+)\s*=\s*\(|let\s+(\w+)\s*=\s*\(|var\s+(\w+)\s*=\s*\()/,
  CLASS_DEF: /class\s+(\w+)/,
  IMPORT_STATEMENT: /(?:import\s+.*from\s+['"]|require\(['"])([^'"]+)/,
  VARIABLE_DECL: /(?:const|let|var)\s+(\w+)/,
  
  // Text patterns
  EMAIL: /\b[A-Za-z0-9._%+-]+@[A-Za-z0-9.-]+\.[A-Z|a-z]{2,}\b/,
  URL: /(?:https?:\/\/|ftp:\/\/|www\.)[^\s/$.?#].[^\s]*/,
  PHONE: /\b(?:\+\d{1,3}[- ]?)?\(?\d{3}\)?[- ]?\d{3}[- ]?\d{4}\b/,
  IP_ADDRESS: /\b(?:\d{1,3}\.){3}\d{1,3}\b/,
  
  // Data patterns
  JSON_OBJECT: /^\s*\{[\s\S]*\}\s*$/,
  JSON_ARRAY: /^\s*\[[\s\S]*\]\s*$/,
  XML_TAG: /<[a-zA-Z_][^>]*>/,
  YAML_DOC: /^---\s*$/,
  
  // Programming language specific
  JAVASCRIPT: /(?:function\s+\w+|const\s+\w+\s*=\s*\(|let\s+\w+\s*=\s*\(|var\s+\w+\s*=\s*\(|=>|import\s+|export\s+|console\.log)/,
  TYPESCRIPT: /(?:interface\s+\w+|type\s+\w+|:\s*\w+;|as\s+\w+)/,
  PYTHON: /(?:def\s+\w+|import\s+\w+|from\s+\w+|print\(|#.*|""".*?"""|'''.*?''')/,
  POWERSHELL: /(?:Get-|Set-|Write-|Start-|Stop-|\$[a-zA-Z_]+|\$ErrorActionPreference)/,
  HTML: /<[a-zA-Z][^>]*>|<\/[a-zA-Z][^>]*>/,
  CSS: /[a-zA-Z\-]+\s*\{[^}]*\}/,
  SQL: /(?:SELECT\s+|INSERT\s+INTO|UPDATE\s+|DELETE\s+FROM|CREATE\s+TABLE)/i,
};

